import React, { Component } from "react";
import Audio from "../Audio/Audio";
import "./RightSidebar.scss";

export default class RightSidebar extends Component {
  state = {
    openRightSidebar: false,
  };
  handleOpenSidebar = () => {
    const { openRightSidebar } = this.state;
    this.setState({
      openRightSidebar: !openRightSidebar,
    });
  };
  render() {
    const { openRightSidebar } = this.state;
    return (
      <div
        className={
          openRightSidebar ? "right-sidebar minimize-sidebar" : "right-sidebar"
        }
      >
        <div className="right-sidebar-header">Listen Special</div>
        <div
          className="right-sidebar-body ps ps--active-y"
          data-scrollable="true"
        >
          <ul className="list-group list-group-flush">
            <li className="custom-list--item list-group-item">
              <div className="text-dark custom-card--inline amplitude-song-container amplitude-play-pause amplitude-paused"></div>
            </li>
          </ul>
        </div>
        <Audio handleOpenSidebar={this.handleOpenSidebar} />
      </div>
    );
  }
}
