import Axios from "axios";
import React, { Component } from "react";
import artistImage from "../../../Assets/image/sliderImage/1.jpg";
import "./ArtistDetails.scss";
import { artistsDetails } from "../../../Actions";
import { connect } from "react-redux";
class ArtistDetails extends Component {
  state = {
    tabValue: "topSongs",
    artistDetails: [
      {
        songNo: "1",
        songImage: artistImage,
        songName: "Bekhudi",
        artistName: "Darshan Raval",
        duration: "05:10",
      },
      {
        songNo: "2",
        songImage: artistImage,
        songName: "Mirchi Mirchi",
        artistName: " Divine",
        duration: "05:42",
      },
      {
        songNo: "3",
        songImage: artistImage,
        songName: "Sanam Re",
        artistName: "Arjit Singh",
        duration: "05:30",
      },
      {
        songNo: "4",
        songImage: artistImage,
        songName: "Ek Raat",
        artistName: "Vilen",
        duration: "05:45",
      },
      {
        songNo: "5",
        songImage: artistImage,
        songName: "Zubi Dubi",
        artistName: "Taare jamin par",
        duration: "05:52",
      },
    ],
  };
  componentDidMount() {
    this.artistDetails();
  }

  artistDetails = () => {
    this.props.artistsDetails(this.props.match.params.artistName);
  };

  handleTabChange = (tabName) => {
    this.setState({
      tabValue: tabName,
    });
  };
  render() {
    const artistData = this.props.artistDetails.songs;
    const { tabValue, artistDetails } = this.state;
    return artistData.map((data, index) => (
      <div>
        <div className="row section text-centre text-md-left">
          <div className="col-xl-3 col-lg-4 col-sm-5 song-header-image">
            <img
              src={artistImage}
              alt="artist-image"
              className="card-img--radius-lg"
            />
          </div>
          <div className="col-xl-9 col-lg-8 col-sm-7">
            <div className="row pt-4">
              <div className="col-xl-8 col-lg-6">
                <h5 className="artist-name">
                  {this.props.match.params.artistName}
                </h5>
                <p className="artist-dob">DOB: 12/03/1996</p>
                <div className="mt-4">
                  <button className="btn btn-pill btn-air btn-bold btn-danger play-all">
                    Play all
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="section">
          <div className="custom-list">
            <ul className="s_l s_h desktopflex" style={{ margin: "0" }}>
              <li className="s_cnt">
                <span className="row-head">#</span>
              </li>
              <li className="s_title">
                <span className="row-head">Title</span>
              </li>
              <li className="s_artist">
                <span className="row-head">Artists</span>
              </li>
              <li className="s_duration">
                <span className="row-head">
                  <i class="far fa-clock"></i>
                </span>
              </li>
            </ul>
            <ul style={{ listStyle: "none", padding: "0" }}>
              <li key={index}>
                <ul className="a_l artworkload _cursor">
                  <li className="s_cnt p_cnt desktop">
                    <span className="_c sng_c">{data.songNo}</span>
                  </li>
                  <li className="s_title p_title list loaded">
                    <div className="playlist-data">
                      <div className="playlist_thumb">
                        <img src={data.songImage} alt="song_image" />
                      </div>
                      <div className="playlist_thumb_det">
                        <span className="songs">{data.songName}</span>
                      </div>
                    </div>
                  </li>
                  <li className="s_artist p_artist desktop">
                    <div>
                      <span className="sng_c">{data.artistName}</span>
                    </div>
                  </li>
                  <li className="s_duration">
                    <span className="desktop sng_c">{data.duration}</span>
                  </li>
                </ul>
              </li>
            </ul>
          </div>
        </div>
      </div>
    ));
  }
}
const MapStateToProps = (state) => ({
  artistDetails: state.home.artistDetails,
});

export default connect(MapStateToProps, { artistsDetails })(ArtistDetails);
