import React, { Component } from "react";
import "./Profile.scss";
import profile from "../../../Assets/image/profile.jpg";
import { connect } from "react-redux";
import DropzoneComponent from "react-dropzone-component";
// import { setUserData } from "../../../Actions";
import { Dialog } from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
import Dropzone from "../../ReusableComponents/Dropzone/Dropzone";
import { uploadFileToServer } from "../../../Actions";

class Profile extends Component {
  state = {
    openDialogBox: false,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    backgroundImage: "",
  };

  handleClickOpen = () => {
    this.setState({
      openDialogBox: true,
    });
  };

  handleClose = () => {
    this.setState({
      openDialogBox: false,
    });
  };
  handleFileUpload = (file, id) => {
    console.log("hello123");
    this.setState({
      [id]: file,
      isDisabled: false,
    });
  };
  handleRemoveFile = (id) => {
    console.log("hello");
    this.setState({
      [id]: "",
    });
  };

  handleChange = (e) => {
    var element = e.target.name;
    let inputVal = e.target.value;
    this.setState({
      [element]: inputVal,
    });
  };

  onRadioHandler = (e) => {
    this.setState({
      gender: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    let { firstName, lastName, gender, email, backgroundImage } = this.state;

    let payload = {
      firstName: firstName,
      lastName: lastName,
      gender: gender,
      email: email,
      backgroundImage: backgroundImage,
    };
    console.log(api_payload);
    if (typeof this.state.backgroundImage !== "string") {
      this.handleImageUpload(payload);
    } else {
      this.handleUploadData(payload);
    }
  };

  handleImageUpload = (payload) => {
    let imageWithNewName = this.backgroundImage.renameFile(
      this.state.backgroundImage
    );
    let formData = new FormData();
    formData.append("file", imageWithNewName);
    this.props
      .uploadFileToServer(formData)
      .then((res) => {
        console.log(res);
        // if (res.response.status === 200) {
        //   payload = {
        //     ...payload,
        //     img: res.responseData.response_data.profile_image,
        //   };
        //   this.handleUploadData(payload);
        // }
      })
      .catch((err) => {});
  };

  handleUploadData = (payload) => {
    const { name, university, society, date, place, description } = this.state;
    if (this.state.isEdit) {
      const editPayload = {
        event_name: name,
        university_id: university,
        society_id: society,
        event_date: moment(date).format("MM-DD-YYYY hh:mm A"),
        event_place: place,
        event_description: description,
        img: this.getImageName(payload.img),
      };
      this.props
        .updateEventByID(this.props.match.params.id, editPayload)
        .then((res) => {
          if (res.response.status === 200) {
            this.setState({
              isLoading: false,
            });
            this.props.history.push("/events");
          } else {
            this.setState({
              alert: res.responseData.response_data.error,
              alertStyle: "danger",
              isLoading: false,
            });
          }
        })
        .catch((err) => {});
    } else {
      this.props
        .addEvent(payload)
        .then((res) => {
          if (res.response.status === 200) {
            this.setState({
              success: true,
              isLoading: false,
            });
            this.context.hideLoader();
          } else {
            this.setState({
              alert: res.responseData.response_data.error,
              alertStyle: "danger",
              isLoading: false,
            });
          }
        })
        .catch((err) => {});
    }
  };

  render() {
    const {
      openDialogBox,
      backgroundImage,
      firstName,
      lastName,
      gender,
      email,
    } = this.state;
