import React, { Component } from "react";
import "./Profile.scss";
import profile from "../../../Assets/image/profile.jpg";
import { connect } from "react-redux";
import DropzoneComponent from "react-dropzone-component";
// import { setUserData } from "../../../Actions";
import { Dialog } from "@material-ui/core";

class Profile extends Component {
  state = {
    openDialogBox: false,
  };

  handleClickOpen = () => {
    this.setState({
      openDialogBox: true,
    });
  };

  handleClose = () => {
    this.setState({
      openDialogBox: false,
    });
  };

  render() {
    const { openDialogBox } = this.state;
    return (
      <div className="row section">
        <div className="col-xl-10 mx-auto">
          <div className="row">
            <div className="col-xl-12 col-md-12">
              <h1 className="genres-heading">Profile Details</h1>
              <div className="card h-auto" style={{ background: "#343a40" }}>
                <div className="card-body">
                  <form action="#" className="row">
                    <div className="col-8 form-group">
                      <label
                        htmlFor="firstName"
                        className="form-label profile-title"
                      >
                        First Name :
                      </label>
                      <label
                        htmlFor="Name"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.firstName}
                      </label>
                    </div>
                    <div className="col-4 right-button">
                      <button type="button" className="btn btn-primary btn-air">
                        Change Password
                      </button>
                    </div>
                    <div className="col-12 form-group">
                      <label
                        htmlFor="lastName"
                        className="form-label profile-title"
                      >
                        Last Name :
                      </label>
                      <label
                        htmlFor="lastName"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.lastName}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label
                        htmlFor="lastName"
                        className="form-label profile-title"
                      >
                        Gender :
                      </label>
                      <label
                        htmlFor="lastName"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.gender}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label
                        htmlFor="lastName"
                        className="form-label profile-title"
                      >
                        Email :
                      </label>
                      <label
                        htmlFor="lastName"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.email}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label className="form-label profile-title">
                        Profile Pic
                      </label>
                    </div>
                    <div className="col-12">
                      <button type="button" className="btn btn-primary btn-air">
                        Save Changes
                      </button>
                    </div>
                  </form>
                  <Dialog open={openDialogBox} onClose={this.handleClose}>
                    <div className="modal-content">
                      <div className="dialog-header">
                        <div>
                          <h5 className="modal-title mb-1">Language</h5>
                          <p className="text-muted">
                            Please select the language(s) of the music you
                            listen to.
                          </p>
                        </div>
                        <button
                          style={{ outline: "none" }}
                          type="button"
                          className="close"
                          onClick={() => this.handleClose()}
                        >
                          <span>x</span>
                        </button>
                      </div>
                      <div className="modal-body">
                        <ul className="list-item-group row">
                          {launguageData.map((data, index) => {
                            return (
                              <li
                                className="list-group-item border-0 col-sm-6 border-none"
                                key={index}
                              >
                                <div className="custom-control custom-checkbox">
                                  <input
                                    type="checkbox"
                                    className="custom-control-input"
                                    id={data.id}
                                    checked={data.checked}
                                  />
                                  <label
                                    htmlFor={data.id}
                                    className="custom-control-label"
                                  >
                                    {data.launguageType}
                                  </label>
                                </div>
                              </li>
                            );
                          })}
                        </ul>
                      </div>
                      <div className="modal-footer text-center d-block">
                        <button
                          type="button"
                          className="btn btn-primary btn-pill"
                        >
                          Apply
                        </button>
                      </div>
                    </div>
                  </Dialog>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const MapStateToProps = (state) => ({
  profileData: state.auth.userData,
});

export default connect(MapStateToProps)(Profile);
