        id: "ch15",
      },
      {
        launguageType: "Odia",
        id: "ch16",
      },
    ],
  };

  logoutUser = () => {
    this.props.logoutUser();
    this.props.history.push("/home-page");
  };

  handleClickOpen = () => {
    this.setState({
      openDialogBox: true,
    });
  };

  handleClose = () => {
    this.setState({
      openDialogBox: false,
    });
  };

  handleOpenProfile = () => {
    this.setState({
      openProfile: !this.state.openProfile,
    });
  };
  hidePopover = () => {
    this.handleOpenProfile();
  };
  componentWillMount() {
    document.addEventListener("mousedown", this.popupActionClick, false);
  }
  componentWillUnmount() {
    document.removeEventListener("mousedown", this.popupActionClick, false);
  }
  popupActionClick = (e) => {
    if (this.node.contains(e.target)) {
      this.setState({
        dropdownActionVisible: true,
      });
      return true;
    }
    this.setState({
      dropdownActionVisible: false,
    });
  };
  getData = (songData, index) => {
    this.props.setMusicData(songData, index);
  };
  setSearchData = (key) => {
    this.setState(
      {
        searchText: key,
        suggestionBox: true,
      },
      () => {
        this.props.getSearchAll(this.state.searchText);
        if (this.state.searchText === "") {
          this.setState({
            suggestionBox: false,
          });
        }
      }
    );
  };

  openSuggestionBox = () => {
    this.setState({
      suggestionBox: false,
      searchText: "",
    });
  };
  render() {
    const {
      openProfile,
      openDialogBox,
      launguageData,
      dropdownActionVisible,
      suggestionBox,
      searchText,
