import React, { Component } from "react";
import { NavLink, Router } from "react-router-dom";
import logo from "../../../assets/logo.svg";

export default class Sidebar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sidebar: false,
    };
  }
  setSideBar = (e) => {
    const { sidebar } = this.state;
    this.setState({
      sidebar: !sidebar,
    });
  };
  render() {
    const { sidebar } = this.state;
    return (
      <Router>
        <div>
          <aside
            // id="sidebar"
            className={sidebar ? "sidebar-main active" : "sidebar-main"}
          >
            <div className="sidebar-header d-flex align-items-center">
              {!sidebar ? <img src={logo} alt="main-logo" /> : ""}
              <button
                type="button"
                className={
                  sidebar ? "btn toggle-menu margin-left" : "btn toggle-menu"
                }
                onClick={(e) => this.setSideBar(e)}
              >
                <span></span>
                <span></span>
                <span></span>
              </button>
            </div>
            <nav className="navbar">
              <ul className="navbar-nav ps ps--active-y" data-scrollable="true">
                <li
                  className={
                    !sidebar ? "nav-item nav-header" : "nav-header disp-none"
                  }
                >
                  Browse Music
                </li>
                <li className="nav-item">
                  <NavLink to={`/home`} className="nav-link">
                    <i
                      className={sidebar ? "la la-home big" : "la la-home"}
                    ></i>
                    <span className={sidebar ? "disp-none" : ""}>Home</span>
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to={`/genres`} className="nav-link">
                    <i
                      className={
                        sidebar ? "la la-diamond big" : "la la-diamond"
                      }
                    ></i>
                    <span className={sidebar ? "disp-none" : ""}>Genres</span>
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to={`/music`} className="nav-link">
                    <i
                      className={sidebar ? "la la-music big" : "la la-music "}
                    ></i>
                    <span className={sidebar ? "disp-none" : ""}>
                      Free Music
                    </span>
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to={`/artists`} className="nav-link">
                    <i
                      className={
                        sidebar ? "la la-microphone big" : "la la-microphone"
                      }
                    ></i>
                    <span className={sidebar ? "disp-none" : ""}>Artists</span>
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to={`/stations`} className="nav-link">
                    <i
                      className={
                        sidebar ? "la la-bullseye big" : "la la-bullseye"
                      }
                    ></i>
                    <span className={sidebar ? "disp-none" : ""}>Stations</span>
                  </NavLink>
                </li>
              </ul>
            </nav>
          </aside>
        </div>
      </Router>
    );
  }
}
