import {
  TOP_CHART_MUSIC,
  NEW_RELEASES,
  SONG_DATA,
  RETRO_CLASSIC,
  RADIO_MUSIC,
} from "../ActionTypes/ActionTypes";

const initialState = {
  topCharts: [],
  newReleases: [],
  songData: [],
  retroClassic: [],
};

export default function (state = initialState, action) {
  switch (action.type) {
    case TOP_CHART_MUSIC:
      return {
        ...state,
        topCharts: action.payload,
      };
    case NEW_RELEASES:
      return {
        ...state,
        newReleases: action.payload,
      };
    case SONG_DATA:
      return {
        ...state,
        songData: action.payload,
      };
    case RETRO_CLASSIC:
      return {
        ...state,
        retroClassic: action.payload,
      };
    case RADIO_MUSIC:
      return {
        ...state,
        retroClassic: action.payload,
      };

    default:
      return state;
  }
}
