import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import "./Home.scss";
import one from "../../../Assets/image/sliderImage/1.jpg";
import two from "../../../Assets/image/sliderImage/2.jpg";
import three from "../../../Assets/image/sliderImage/3.jpg";
import four from "../../../Assets/image/sliderImage/4.jpg";
import five from "../../../Assets/image/sliderImage/5.jpg";
import six from "../../../Assets/image/sliderImage/6.jpg";
import Carousel from "../../ReusableComponents/Carousel/CustomeCarousel";

import remix from "../../../Assets/image/Genres/aa.jpg";
import rock from "../../../Assets/image/Genres/bb.jpg";
import sufi from "../../../Assets/image/Genres/cc.jpg";
import romantic from "../../../Assets/image/Genres/dd.jpg";
import sports from "../../../Assets/image/Genres/ee.jpg";
import old from "../../../Assets/image/Genres/ff.jpg";
import ActionPopover from "../../ReusableComponents/ActionPopover/ActionPopover";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tabValue: "Recent",
      showPopover: false,
      currentIndex: 0,
      topCharts: [
        {
          id: 1,
          songName: "I Love You Mummy",
          artist: "Arebica Luna",
          path: one,
        },
        {
          id: 2,
          songName: "Shack your butty",
          artist: "Gerrina Linda",
          path: two,
        },
        {
          id: 3,
          songName: "Do it your way(Female)",
          artist: "Zunira Willy & Nutty Nina",
          path: three,
        },
        {
          id: 4,
          songName: "Say yes",
          artist: "Johnny Marro",
          path: four,
        },
        {
          id: 5,
          songName: "Where is your letter",
          artist: "Jina Moore & Lenisa Gory",
          path: five,
        },
        {
          id: 6,
          songName: "Hey not me",
          artist: "Rasomi Pelina",
          path: six,
        },
      ],
      genres: [
        {
          id: 1,
          path: remix,
          genresType: "Remix Songs",
          playlistType: "Dj Remix",
        },
        {
          id: 2,
          path: rock,
          genresType: "Rock Songs",
          playlistType: "Rock Band",
        },
        {
          id: 3,
          path: sufi,
          genresType: "Sufi Songs",
          playlistType: "Solo Special",
        },
        {
          id: 4,
          path: romantic,
          genresType: "Romantic Songs",
          playlistType: "Romantic",
        },
        {
          id: 5,
          path: sports,
          genresType: "Sports Songs",
          playlistType: "GYM",
        },
        {
          id: 6,
          path: old,
          genresType: "Old Songs",
          playlistType: "Retro Special",
        },
      ],
      responsive1: {
        0: {
          items: 1,
        },
        379: {
          items: 2,
        },
        639: {
          items: 3,
        },
        1199: {
          items: 4,
        },
        1439: {
          items: 5,
        },
      },
      responsive2: {
        0: {
          items: 1,
        },
        379: {
          items: 2,
        },
        639: {
          items: 4,
        },
        1199: {
          items: 5,
        },
        1439: {
          items: 6,
        },
      },
      responsive3: {
        0: {
          items: 1,
        },
        639: {
          items: 2,
        },
        1199: {
          items: 3,
        },
        1439: {
          items: 4,
        },
      },
    };
  }

  handleShowPopOver = (index) => {
    console.log(index);
    this.setState({
      showPopover: !this.state.showPopover,
      currentIndex: index,
    });
  };

  handleTabChange = (tabName) => {
    console.log(tabName);
    this.setState({
      tabValue: tabName,
    });
  };

  render() {
    const { tabValue, topCharts, showPopover, currentIndex } = this.state;
    let transform;
    if (currentIndex === 0) {
      transform = "translate3d(210px, 70px, 0px)";
    } else if (currentIndex === 1) {
      transform = "translate3d(210px, 125px, 0px)";
    } else if (currentIndex === 2) {
      transform = "translate3d(210px, 180px, 0px)";
    } else if (currentIndex === 3) {
      transform = "translate3d(210px, 235px, 0px)";
    } else if (currentIndex === 4) {
      transform = "translate3d(210px, 280px, 0px)";
    } else if (currentIndex === 5) {
      transform = "translate3d(210px, 2335px, 0px)";
    }
    return (
      <div>
        <div className="section-1">
          <div className="top-header">
            <div className="d-flex flex-wrap align-items-end">
              <div className="flex-grow-1">
                <h4 className="top-charts">Top Charts</h4>
                <p className="listen-chart">Listen top charts</p>
              </div>
              <NavLink
                to={`/homej`}
                className="btn btn-sm btn-pill btn-air btn-color"
              >
                View All
              </NavLink>
            </div>
            <hr />
          </div>
        </div>
        <div className="image-slide">
          <Carousel
            imagePath={this.state.topCharts}
            margin={30}
            imageSize="image-radius"
            responsive={this.state.responsive1}
            showPopover={true}
          />
        </div>
        <div className="row-music">
          <div className=" section col-xl-7 col-lg-6">
            <div className="row h-100">
              <div className="col-sm-5 pb-4">
                <div className="h-100 event event-v bg-img bg-img-radius-lg image-url"></div>
              </div>
              <div className="col-sm-7">
                <div className="h-50 pb-4">
                  <div className="h-100 event event-h bg-img bg-img-radius-lg back-img">
                    <div className="event-content p-4">
                      <h6 className="image-head">Dance with DJ Nowan</h6>
                      <p className="image-desc">
                        Lorem ipsum dolor sit amet, consectetur adipisicing
                        elit. Beatae consectetur, ex explicabo.
                      </p>
                    </div>
                  </div>
                </div>
                <div className="h-50 pb-4">
                  <div className="h-100 event event-h bg-img bg-img-radius-lg back-img2">
                    <div className="event-content p-4">
                      <h6 className="image-head">Move You's Legs</h6>
                      <p className="image-desc">
                        Lorem ipsum dolor sit amet, consectetur adipisicing
                        elit. Beatae consectetur, ex explicabo.
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="section col-xl-5 col-lg-6">
            <ul
              className="nav nav-tabs line-tabs line-tabs-primary text-uppercase mb-4 ul-cursor"
              id="songsList"
              role="tablist"
            >
              <li
                className="nav-item"
                onClick={() => this.handleTabChange("Recent")}
              >
                <a
                  className="nav-link link-color"
                  id="recent-tab"
                  data-toggle="tab"
                  role="tab"
                  aria-controls="recent"
                >
                  Recent
                </a>
              </li>
              <li
                className="nav-item"
                onClick={() => this.handleTabChange("Trending")}
              >
                <a
                  className="nav-link link-color"
                  id="trending-tab"
                  data-toggle="tab"
                  role="tab"
                  aria-controls="trending"
                >
                  Trending
                </a>
              </li>
              <li
                className="nav-item"
                onClick={() => this.handleTabChange("International")}
              >
                <a
                  className="nav-link link-color"
                  id="international-tab"
                  data-toggle="tab"
                  role="tab"
                  aria-controls="international"
                >
                  International
                </a>
              </li>
              <span
                className={
                  tabValue === "Recent"
                    ? "tabs-link-line recent-width"
                    : "tabs-link-line trending-width" &&
                      tabValue === "International"
                    ? "tabs-link-line international-width"
                    : "tabs-link-line trending-width"
                }
              ></span>
            </ul>
            <div className="tab-content" id="songsListContent">
              {tabValue === "Recent" && (
                <div
                  // className="tab-pane fade active show"
                  id="recent"
                  role="tabpanel"
                  aria-labelledby="recent-tab"
                >
                  <div className="custom-list">
                    {topCharts &&
                      topCharts.map((data, index) => {
                        return (
                          <div className="custom-item" key={index}>
                            <div className="text-dark custom-card--inline">
                              <div className="custom-card--inline-img">
                                <img
                                  src={data.path}
                                  alt="song-image"
                                  className="card-img--radius-sm"
                                />
                              </div>
                              <div className="custom-card--inline-desc">
                                <p className="text-truncate mb-0 song-color pd-11">
                                  {data.songName}
                                </p>
                                <p className="text-truncate text-muted font-sm">
                                  {data.artist}
                                </p>
                              </div>
                            </div>
                            <ul className="custom-card--labels d-flex ml-auto">
                              <li className="dropleft">
                                <button
                                  className="btn btn-icon-only"
                                  onClick={(e) => this.handleShowPopOver(index)}
                                >
                                  <span
                                    className="iconify three-dot-action"
                                    data-icon="fe-elipsis-h"
                                    data-inline="false"
                                  ></span>
                                </button>
                              </li>
                            </ul>
                          </div>
                        );
                      })}
                  </div>
                  <ActionPopover
                    dropdownExpand={showPopover}
                    transform={transform}
                  />
                </div>
              )}
              {tabValue === "Trending" && (
                <div id="recent" role="tabpanel" aria-labelledby="recent-tab">
                  <div className="custom-list">
                    {topCharts &&
                      topCharts.map((data, index) => {
                        return (
                          <div className="custom-item" key={index}>
                            <div className="text-dark custom-card--inline">
                              <div className="custom-card--inline-img">
                                <img
                                  src={data.path}
                                  alt="song-image"
                                  className="card-img--radius-sm"
                                />
                              </div>
                              <div className="custom-card--inline-desc">
                                <p className="text-truncate mb-0 song-color pd-11">
                                  {data.songName}
                                </p>
                                <p className="text-truncate text-muted font-sm">
                                  {data.artist}
                                </p>
                              </div>
                            </div>
                          </div>
                        );
                      })}
                  </div>
                </div>
              )}
              {tabValue === "International" && (
                <div
                  // className="tab-pane fade"
                  id="recent"
                  role="tabpanel"
                  aria-labelledby="recent-tab"
                >
                  <div className="custom-list">
                    {topCharts &&
                      topCharts.map((data, index) => {
                        return (
                          <div className="custom-item" key={index}>
                            <div className="text-dark custom-card--inline">
                              <div className="custom-card--inline-img">
                                <img
                                  src={data.path}
                                  alt="song-image"
                                  className="card-img--radius-sm"
                                />
                              </div>
                              <div className="custom-card--inline-desc">
                                <p className="text-truncate mb-0 song-color pd-11">
                                  {data.songName}
