import React, { Component } from "react";
import "./Profile.scss";
import profile from "../../../Assets/image/profile.jpg";
import { connect } from "react-redux";
import DropzoneComponent from "react-dropzone-component";
// import { setUserData } from "../../../Actions";

class Profile extends Component {
  handleDroppedFile(file) {
    var reader = new FileReader();
    let that = this;
    reader.addEventListener(
      "load",
      function () {
        that.setState({
          originalFile: reader.result,
          fileName: file.name,
          showModal: true,
        });
      },
      false
    );
    if (file) {
      reader.readAsDataURL(file);
    }
  }
  render() {
    var componentConfig = {
      iconFiletypes: [".jpg", ".png"],
      showFiletypeIcon: true,
      postUrl: "no-url",
    };
    let djsConfig = {
      autoProcesQueue: false,
      maxFiles: 1,
      acceptedFiles: "image/*",
    };
    var eventHandlers = {
      init: this.initCallback,
      addedfile: (file) => this.handleDroppedFile(file),
    };
    return (
      <div className="row section">
        <div className="col-xl-10 mx-auto">
          <div className="row">
            <div className="col-xl-12 col-md-12">
              <h1 className="genres-heading">Profile Details</h1>
              <div className="card h-auto" style={{ background: "#343a40" }}>
                <div className="card-body">
                  <form action="#" className="row">
                    <div className="col-12 form-group">
                      <label
                        htmlFor="firstName"
                        className="form-label profile-title"
                      >
                        First Name :
                      </label>
                      <label
                        htmlFor="Name"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.firstName}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label
                        htmlFor="lastName"
                        className="form-label profile-title"
                      >
                        Last Name :
                      </label>
                      <label
                        htmlFor="lastName"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.lastName}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label
                        htmlFor="lastName"
                        className="form-label profile-title"
                      >
                        Gender :
                      </label>
                      <label
                        htmlFor="lastName"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.gender}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label
                        htmlFor="lastName"
                        className="form-label profile-title"
                      >
                        Email :
                      </label>
                      <label
                        htmlFor="lastName"
                        className="form-label"
                        style={{ color: "#adb5bd", paddingLeft: "5px" }}
                      >
                        {this.props.profileData.email}
                      </label>
                    </div>
                    <div className="col-12 form-group">
                      <label className="form-label profile-title">
                        Profile Pic
                      </label>
                      <DropzoneComponent
                        config={componentConfig}
                        eventHandlers={eventHandlers}
                        djsConfig={djsConfig}
                      />
                      ,
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const MapStateToProps = (state) => ({
  profileData: state.auth.userData,
});

export default connect(MapStateToProps)(Profile);
