import React, { Component } from "react";
import "./Audio.scss";
import AudioPlayer, { RHAP_UI } from "react-h5-audio-player";
import "react-h5-audio-player/lib/styles.css";
import ReactAudioPlayer from "react-audio-player";
import songImage from "../../../Assets/image/sliderImage/1.jpg";
import { Icon, InlineIcon } from "@iconify/react";
import mdPlay from "@iconify-icons/ion/md-play";
import mdSync from "@iconify-icons/ion/md-sync";
import musicalNote from "@iconify-icons/ion/musical-note";
import music from "../../../Assets/Musics/test.mp3";
import mdVolumeLow from "@iconify-icons/ion/md-volume-low";

export default class Audio extends Component {
  state = {
    dropdownExpand: false,
    volumeSlider: false,
  };
  handleDropdownChange = () => {
    this.setState({
      dropdownExpand: !this.state.dropdownExpand,
    });
  };
  openVolumeSlider = () => {
    console.log("yes");
    this.setState({
      volumeSlider: true,
    });
  };
  render() {
    const { dropdownExpand } = this.state;
    const fullWidth = this.props;
    let audioClass;
    if (fullWidth.fullWidth) {
      audioClass = "audioPlayer long-player";
    } else {
      audioClass = "audioPlayer";
    }
    return (
      <div className={audioClass}>
        <div className="audio">
          <div className="song-image">
            <img src={songImage} alt="song-image" className="song-img" />
          </div>
          <div className="song-info pl-3">
            <span className="song-title">I Love You Mummy</span>
            <span className="song-artist">Arebica Luna</span>
          </div>
        </div>
        <div className="audio-controls">
          <div className="audio-controls--left d-flex mr-auto">
            <AudioPlayer
              // src="https://assets.coderrocketfuel.com/pomodoro-times-up.mp3"
              src="{music}"
              showSkipControls={true}
              showJumpControls={false}
              customProgressBarSection={[RHAP_UI.PROGRESS_BAR]}
              customIcons={{
                volume: (
                  <button
                    aria-label="Unmute"
                    className="btn btn-icon-only"
                    onClick={this.openVolumeSlider}
                  >
                    <span
                      className="iconify audio-player-icon"
                      data-icon="ion-md-volume-low"
                      data-inline="false"
                    ></span>
                  </button>
                ),
                volumeMute: (
                  <button className="btn btn-icon-only" aria-label="Unmute">
                    <span
                      className="iconify audio-player-icon"
                      data-icon="ion-md-volume-low"
                      data-inline="false"
                    ></span>
                  </button>
                ),
              }}
              customControlsSection={[
                <button className="btn btn-icon-only">
                  <span
                    className="audio-player-icon iconify"
                    data-icon="ion-md-sync"
                    data-inline="false"
                  ></span>
                </button>,
                RHAP_UI.MAIN_CONTROLS,
                <button className="btn btn-icon-only amplitude-shuffle amplitude-shuffle-on">
                  <span
                    className="audio-player-icon iconify"
                    data-icon="ion-md-shuffle"
                    data-inline="false"
                  ></span>
                </button>,
                RHAP_UI.CURRENT_TIME,
                <div className="or-arrow"> / </div>,
                RHAP_UI.DURATION,
                RHAP_UI.VOLUME_CONTROLS,
              ]}
            />
          </div>
        </div>
        <div className="audio-info">
          <div className="dropleft">
            <button
              className="btn btn-icon-only"
              onClick={this.handleDropdownChange}
            >
              <span
                className="iconify audio-player-icon"
                data-icon="fe:elipsis-v"
                data-inline="false"
              ></span>
            </button>
            <ul
              className={
                dropdownExpand ? "dropdown-menu show" : "dropdown-menu"
              }
              x-placement="left-start"
              style={{
                top: "0px",
                position: "absolute",
                willChange: "transform",
                left: "0px",
                transform: "translate3d(-162px, -143px, 0px)",
              }}
            >
              <li className="dropdown-item">
                <i className="la la-heart-o dropdown-icon"></i>
                <span>Favorite</span>
              </li>
              <li className="dropdown-item">
                <i className="la la-plus dropdown-icon"></i>
                <span>Add to Playlist</span>
              </li>
              <li className="dropdown-item">
                <i className="la la-download dropdown-icon"></i>
                <span>Download</span>
              </li>
              <li className="dropdown-item">
                <i className="la la-share-alt dropdown-icon"></i>
                <span>Share</span>
              </li>
              <li className="dropdown-item">
                <i className="la la-info-circle dropdown-icon"></i>
                <span>Song info</span>
              </li>
            </ul>
          </div>
          <button className="btn btn-icon-only">
            <Icon className="music-icon" icon={musicalNote} />
          </button>
        </div>
      </div>
    );
  }
}
