import React, { Component } from "react";
import "./Profile.scss";
import profile from "../../../Assets/image/profile.jpg";
import { connect } from "react-redux";
import DropzoneComponent from "react-dropzone-component";
// import { setUserData } from "../../../Actions";
import { Dialog } from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
// import Dropzone from "../../ReusableComponents/Dropzone/Dropzone";
import { uploadFileToServer, updateProfileDetails } from "../../../Actions";
import global from "../../../global";
import "../../../../node_modules/react-dropzone-component/styles/filepicker.css";
import "../../../../node_modules/dropzone/dist/min/dropzone.min.css";

class Profile extends Component {
  static contextType = global.LoaderContext;
  state = {
    profileId: "",
    openDialogBox: false,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    profileImage: "",
  };
  componentDidMount() {
    let firstName = localStorage.getItem("firstName");
    let lastName = localStorage.getItem("lastName");
    let email = localStorage.getItem("email");
    let gender = localStorage.getItem("gender");
    let id = localStorage.getItem("id");
    this.setState({
      firstName: firstName,
      lastName: lastName,
      email: email,
      gender: gender,
      profileId: id,
    });
  }

  handleClickOpen = () => {
    this.setState({
      openDialogBox: true,
    });
  };

  handleClose = () => {
    this.setState({
      openDialogBox: false,
    });
  };

  handleFileUpload = (file, id) => {
    console.log("handleFileUpload");
    this.setState({
      [id]: file,
      isDisabled: false,
    });
  };

  handleRemoveFile = (id) => {
    console.log("handleRemoveFile");
    this.setState({
      [id]: "",
    });
  };

  handleChange = (e) => {
    var element = e.target.name;
    let inputVal = e.target.value;
    this.setState({
      [element]: inputVal,
    });
  };

  onRadioHandler = (e) => {
    this.setState({
      gender: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    let {
      firstName,
      lastName,
      gender,
      email,
      profileImage,
      profileId,
    } = this.state;

    let payload = {
      firstName: firstName,
      lastName: lastName,
      gender: gender,
      email: email,
      profileImage: profileImage,
    };
    let data = new FormData();
    data.append("profileImage", profileImage);
    this.props.uploadFileToServer();
    this.props.updateProfileDetails(profileId, payload);
    // console.log(api_payload);
    // if (typeof this.state.profileImage !== "string") {
    //   this.handleImageUpload(payload);
    // } else {
    //   this.handleUploadData(payload);
    // }
  };

  handleImageUpload = (payload) => {
    let imageWithNewName = this.profileImage.renameFile(
      this.state.profileImage
    );
    let formData = new FormData();
    formData.append("file", imageWithNewName);
    this.props
      .uploadFileToServer(formData)
      .then((res) => {
        console.log(res);
        // if (res.response.status === 200) {
        //   payload = {
        //     ...payload,
        //     img: res.responseData.response_data.profile_image,
        //   };
        //   this.handleUploadData(payload);
        // }
      })
      .catch((err) => {});
  };

  handleDroppedFile(file) {
    this.setState({
      ProfileImage: file,
    });
    var reader = new FileReader();
    let that = this;
