import React, { Component } from "react";
import { Dialog } from "@material-ui/core";
import { loginUser } from "../../Actions";
import { connect } from "react-redux";
import PropTypes from "prop-types";
class SignIn extends Component {
  state = {
    email: "",
    password: "",
    errors: {},
    showLoginValidation: false,
  };

  componentWillReceiveProps(nextProps) {
    console.log(this.props);
    if (this.props.errors !== nextProps.errors) {
      if (nextProps.errors) {
        this.setState({
          errors: nextProps.errors,
        });
      }
    }
  }
  // componentWillReceiveProps(nextProps) {
  //   if (this.props.nextProps.errors) {
  //     this.setState({
  //       errors: nextProps.errors,
  //     });
  //   }
  // }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    const { email, password } = this.state;
    this.setState({
      email: "",
      password: "",
    });
    const userData = {
      email: email,
      password: password,
    };
    this.props.loginUser(userData);
    console.log(this.state.errors);
    // if (this.state.errors.length !== 0) {
    //   console.log(this.state.errors);
    // }
    // this.props.history.push("/home");
  };

  render() {
    const { email, password, showLoginValidation, errors } = this.state;
    return (
      <Dialog open={this.props.openSignIn} className="signin-page">
        <div className="modal-dialog theme-dialog">
          <div className="home-modal-content">
            <div className="modal-header border-bottom-0">
              {showLoginValidation ? (
                <div class="alert alert-danger" role="alert">
                  {errors}
                </div>
              ) : (
                ""
              )}
              <button
                className="close"
                type="button"
                onClick={this.props.handleCloseSignIn}
              >
                x
              </button>
            </div>
            <h6 className="sign-title text-center mb-3 pd-both">
              Sign In For Awesome Listing Experience
            </h6>
            <div className="modal-body">
              <form
                className="mx-4 pb-5 ng-untouched ng-pristine ng-valid"
                onSubmit={this.handleFormSubmit}
              >
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control ng-untouched ng-pristine ng-valid"
                    formcontrolname="userName"
                    name="email"
                    id="userName"
                    placeholder="Email Id"
                    value={email}
                    onChange={this.handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="password"
                    className="form-control ng-untouched ng-pristine ng-valid"
                    formcontrolname="password"
                    id="password"
                    name="password"
                    value={password}
                    placeholder="Password"
                    onChange={this.handleChange}
                    required
                  />
                </div>
                <button
                  className="btn btn-block btn-bold btn-air btn-info load-page"
                  type="submit"
                >
                  Sign in
                </button>
              </form>
            </div>
          </div>
        </div>
      </Dialog>
    );
  }
}
SignIn.propTypes = {
  errors: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  errors: state.errors,
});

export default connect(mapStateToProps, { loginUser })(SignIn);
