import React, { Component } from "react";
import "./Music.scss";
import { connect } from "react-redux";
import { AllSongs } from "../../../Actions";
class Music extends Component {
  componentDidMount() {
    this.AllSongs();
  }
  AllSongs = () => {
    this.props.AllSongs();
  };
  render() {
    return (
      <div>
        <h1 className="genres-heading">Free Music</h1>
        <div className="row align-items-end">
          <span className="col-6 font-weight-bold">1254</span>
          <div className="col-12">
            <hr />
          </div>
        </div>
        <div className="section">
          <div className="custom-list">
            {this.props.songsData &&
              this.props.songsData.map((data, index) => {
                return (
                  <div className="custom-list--item">
                    <div className="text-dark custom-card--inline">
                      <div className="custom-card--inline-img">
                        <img
                          src={data.songImage}
                          alt="song-image"
                          className="card-img--radius-sm"
                        />
                      </div>
                      <div className="custom-card--inline-desc">
                        <p className="text-truncate mb-0 song-color pd-11">
                          {data.songName}
                        </p>
                        <p className="text-truncate text-muted font-sm">
                          {data.artist}
                        </p>
                      </div>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    );
  }
}
const MapStateToProps = (state) => ({
  songsData: state.home.allSongs,
});

export default connect(MapStateToProps, {
  AllSongs,
})(Music);
