import { TOP_CHART_MUSIC, NEW_RELEASES } from "../ActionTypes/ActionTypes";

const initialState = {
  topCharts: [],
  newReleases: [],
  playSong: "http://localhost:4000/uploads/1608011825737.mp3",
};

export default function (state = initialState, action) {
  switch (action.type) {
    case TOP_CHART_MUSIC:
      return {
        ...state,
        topCharts: action.payload,
      };
    case NEW_RELEASES:
      return {
        ...state,
        newReleases: action.payload,
      };
    default:
      return state;
  }
}
