import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import "./Home.scss";
import { connect } from "react-redux";
import {
  topChartMusic,
  newReleaseMusic,
  retroClassicMusic,
  radioMusic,
  featureArtists,
  genres,
  getSongsType,
  setMusicData,
  showLoader,
  hideLoader,
} from "../../../Actions";
import Carousel from "../../ReusableComponents/Carousel/CustomeCarousel";
import ActionPopover from "../../ReusableComponents/ActionPopover/ActionPopover";
import axios from "axios";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tabValue: "Recent",
      showPopover: false,
      currentIndex: 0,
      // topCharts: [
      //   {
      //     id: 1,
      //     songName: "I Love You Mummy",
      //     artist: "Arebica Luna",
      //     songImage: one,
      //   },
      //   {
      //     id: 2,
      //     songName: "Shack your butty",
      //     artist: "Gerrina Linda",
      //     songImage: two,
      //   },
      //   {
      //     id: 3,
      //     songName: "Do it your way(Female)",
      //     artist: "Zunira Willy & Nutty Nina",
      //     songImage: three,
      //   },
      //   {
      //     id: 4,
      //     songName: "Say yes",
      //     artist: "Johnny Marro",
      //     songImage: four,
      //   },
      //   {
      //     id: 5,
      //     songName: "Where is your letter",
      //     artist: "Jina Moore & Lenisa Gory",
      //     songImage: five,
      //   },
      //   {
      //     id: 6,
      //     songName: "Hey not me",
      //     artist: "Rasomi Pelina",
      //     songImage: six,
      //   },
      // ],
      responsive1: {
        0: {
          items: 1,
        },
        379: {
          items: 2,
        },
        639: {
          items: 3,
        },
        1199: {
          items: 4,
        },
        1439: {
          items: 5,
        },
      },
      responsive2: {
        0: {
          items: 1,
        },
        379: {
          items: 2,
        },
        639: {
          items: 4,
        },
        1199: {
          items: 5,
        },
        1439: {
          items: 6,
        },
      },
      responsive3: {
        0: {
          items: 1,
        },
        639: {
          items: 2,
        },
        1199: {
          items: 3,
        },
        1439: {
          items: 4,
        },
      },
      songData: [],
    };
  }

  handleShowPopOver = (index) => {
    this.setState({
      showPopover: !this.state.showPopover,
      currentIndex: index,
    });
  };

  componentDidMount() {
    this.props.showLoader();
    this.topChartMusic();
    this.newReleaseMusic();
    this.retroClassicMusic();
    this.radioMusic();
    this.featureArtist();
    this.genres();
    this.getSongs();
  }
  getSongs = () => {
    this.props.getSongsType("Recent");
  };

  handleTabChange = (tabName) => {
    this.setState(
      {
        tabValue: tabName,
      },
      () => {
        this.props.getSongsType(tabName);
      }
    );
  };

  componentWillReceiveProps(nextProps) {
    if (this.props.songsType !== nextProps.songsType) {
      this.setState({
        songData: nextProps.songsType.songs,
      });
    }
  }

  topChartMusic = () => {
    this.props.topChartMusic();
  };
  newReleaseMusic = () => {
    this.props.newReleaseMusic();
  };
  retroClassicMusic = () => {
    this.props.retroClassicMusic();
  };
  radioMusic = () => {
    this.props.radioMusic();
  };
  featureArtist = () => {
    this.props.featureArtists();
  };
  genres = () => {
    this.props.genres();
  };
  getData = (songData, index) => {
    this.props.setMusicData(songData, index);
  };
  downloadSong = (id, filename) => {
    const url = "http://localhost:4000/songs/download/" + id;

    axios
      .get(url, {
        responseType: "blob",
        headers: {
          Accept: "*/*",
          "Content-Type": "audio/mpeg",
        },
      })
      .then((res) => {
        // fileDownload(res.data, filename);
