import React, { Component } from "react";
import "./Profile.scss";
import profile from "../../../Assets/image/profile.jpg";
import { connect } from "react-redux";
import DropzoneComponent from "react-dropzone-component";
// import { setUserData } from "../../../Actions";
import { Dialog } from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
// import Dropzone from "../../ReusableComponents/Dropzone/Dropzone";
import { uploadFileToServer, updateProfileDetails } from "../../../Actions";
import global from "../../../global";
import "../../../../node_modules/react-dropzone-component/styles/filepicker.css";
import "../../../../node_modules/dropzone/dist/min/dropzone.min.css";
import Dropzone from "react-dropzone";
import defaultImage from "../../../Assets/default.jpg";

class Profile extends Component {
  static contextType = global.LoaderContext;
  state = {
    profileId: "",
    openDialogBox: false,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    profileImage: "",
    image: {
      preview: defaultImage,
    },
  };
  componentDidMount() {
    let id = localStorage.getItem("id");
  }

  handleClickOpen = () => {
    this.setState({
      openDialogBox: true,
    });
  };

  handleClose = () => {
    this.setState({
      openDialogBox: false,
    });
  };

  handleFileUpload = (file, id) => {
    console.log("handleFileUpload");
    this.setState({
      [id]: file,
      isDisabled: false,
    });
  };

  handleRemoveFile = (id) => {
    console.log("handleRemoveFile");
    this.setState({
      [id]: "",
    });
  };

  handleChange = (e) => {
    var element = e.target.name;
    let inputVal = e.target.value;
    this.setState({
      [element]: inputVal,
    });
  };

  onRadioHandler = (e) => {
    this.setState({
      gender: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    let {
      firstName,
      lastName,
      gender,
      email,
      profileImage,
      profileId,
    } = this.state;

    let payload = {
      firstName: firstName,
      lastName: lastName,
      gender: gender,
      email: email,
    };

    const formData = new FormData();
    formData.append("myImage", this.state.profileImage);

    if (profileImage === "") {
      this.props.updateProfileDetails(profileId, payload);
    } else {
      this.props
        .uploadFileToServer(formData)
        .then((res) => {
          console.log("res", res);
          payload.filename = res.responseData.data.filename;
          this.props.updateProfileDetails(profileId, payload);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  onDrop = (file) => {
    let selectFile = file[0];
    // const image = Object.assign(selectFile, {
    //   preview: URL.createObjectURL(selectFile),
    // });

    this.setState({
      profileImage: selectFile,
    });

    this.fileToDataUri(selectFile).then((dataUri) => {
      this.setState({
        image: {
          preview: dataUri,
        },
      });
    });
