import React, { Component } from "react";
import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import "./Carousel.scss";
import { NavLink } from "react-router-dom";

class Carousel extends Component {
  state = {
    openPopover: false,
  };
  handleOpenPopover = () => {
    console.log("Open");
    this.setState({
      openPopover: !this.state.openPopover,
    });
  };
  render() {
    const { openPopover } = this.state;
    const navTest = [
      '<span class="la la-angle-left font-width"></span>',
      '<span class="la la-angle-right "></span>',
    ];

    const imagePath = this.props.imagePath;

    return (
      <div className="container-fluid pd-0">
        <OwlCarousel
          items={this.props.items}
          nav={true}
          margin={this.props.margin}
          autoplay={this.props.autoplay}
          responsiveClass={true}
          responsive={this.props.responsive}
          navText={navTest}
          dots={false}
        >
          {imagePath &&
            imagePath.map((data, index) => {
              return (
                <div className={this.props.imageSize}>
                  <img
                    src={data.path}
                    className={this.props.imageSize}
                    key={index}
                  />
                  {this.props.backgroundBlur ? (
                    <span className="bg-blur">{data.genresType}</span>
                  ) : (
                    ""
                  )}
                  {this.props.showPopover ? (
                    <div
                      className="three-dot-dropdown"
                      onClick={this.handleOpenPopover}
                    >
                      <span
                        className="iconify caursol-three-dotes"
                        data-icon="fe-elipsis-v"
                        data-inline="false"
                      ></span>
                    </div>
                  ) : (
                    ""
                  )}
                  <div className="custom-card--link mt-2">
                    <h6 className="song-name">{data.songName}</h6>
                    <p className="artist">{data.artist}</p>
                  </div>
                  <ul
                    className={
                      openPopover
                        ? "drop-menu dropdown-menu-right "
                        : "drop-menu dropdown-menu-right hide"
                    }
                    style={{
                      position: "absolute",
                      willChange: "transform",
                      top: "0px",
                      left: "0px",
                      transform: "translate3d(-140px, 25px, 0px)",
                    }}
                  >
                    <li className="drop-item ">
                      <span className="dropdown-link">
                        <i className="la la-heart-o popover-icons"></i>
                      </span>
                    </li>
                    <li className="drop-item">
                      <span className="dropdown-link">
                        <i className="la la-plus popover-icons"></i>
                      </span>
                    </li>
                    <li className="drop-item">
                      <span className="dropdown-link">
                        <i className="la la-download popover-icons"></i>
                      </span>
                    </li>
                    <li className="drop-item">
                      <span className="dropdown-link">
                        <i className="la la-share-alt popover-icons"></i>
                      </span>
                    </li>
                  </ul>
                </div>
              );
            })}
        </OwlCarousel>
      </div>
    );
  }
}
export default Carousel;
